StoredProcedureSqlServerv2k5.body:
@Organiza.onID as integer
AS
SELECT RECRUITING_ROLE.RECRUITING_ROLE_ID, RECRUITING_ROLE.SHORT_DESCRIPTION FROM RECRUITING_ORGANIZATION, ORGANIZATION_TYPE, ORGANIZATION_BILLET, RECRUITING_ROLE WHERE RECRUITING_ORGANIZATION.ORGANIZATION_ID = @Organiza.onID AND RECRUITING_ORGANIZATION.ORGANIZATION_TYPE = ORGANIZATION_TYPE.ORGANIZATION_TYPE AND ORGANIZATION_TYPE.ORGANIZATION_TYPE = ORGANIZATION_BILLET.ORGANIZATION_TYPE AND ORGANIZATION_BILLET.RECRUITING_ROLE_ID = RECRUITING_ROLE.RECRUITING_ROLE_ID ORDER BY RECRUITING_ROLE.SHORT_DESCRIPTION
