StoredProcedureSqlServerv2k5.body:
@FiscalYear int, @MCRC int AS
SELECT OMD.CHILD_ORGANIZATION_ID AS ORGANIZATION_ID, MONTH_CODE.MONTH_CODE, CMP.MISSION_VALUE FROM
CONTRACTING_MISSION_PLAN CMP, RECRUITING_ORGANIZATION RO, ORGANIZATION_MAP OMR, ORGANIZATION_MAP OMD, MONTH_CODE WHERE RO.ORGANIZATION_ID = OMD.CHILD_ORGANIZATION_ID AND RO.ORGANIZATION_TYPE = 'D' AND OMR.PARENT_ORGANIZATION_ID = @MCRC AND OMR.CHILD_ORGANIZATION_ID = OMD.PARENT_ORGANIZATION_ID AND OMD.CHILD_ORGANIZATION_ID = CMP.ORGANIZATION_ID AND CMP.FISCAL_YEAR = @FiscalYear AND CMP.MONTH_CODE = MONTH_CODE.MONTH_CODE AND CMP.MONTH_CODE > 0 AND CMP.CONTRACTING_MISSION_TYPE = 'CMC' ORDER BY OMR.CHILD_ORGANIZATION_ID, RO.SHORT_NAME, MONTH_CODE.DISPLAY_ORDER
