StoredProcedureSqlServerv2k5.body:
@ParentOrgID int AS
SELECT RO.ORGANIZATION_ID, RO.SHORT_NAME, RO.ORGANIZATION_TYPE, RO.ISOPEN, RO.EFFECTIVE_DATE FROM RECRUITING_ORGANIZATION RO INNER JOIN ORGANIZATION_MAP OM ON RO.ORGANIZATION_ID = OM.CHILD_ORGANIZATION_ID WHERE PARENT_ORGANIZATION_ID = @ParentOrgID AND ORGANIZATION_TYPE <> 'OSS' ORDER BY ISOPEN DESC, SHORT_NAME
/* SELECT ORGANIZATION_ID, SHORT_NAME, ORGANIZATION_TYPE, ISOPEN FROM RECRUITING_ORGANIZATION INNER JOIN ORGANIZATION_MAP ON RECRUITING_ORGANIZATION.ORGANIZATION_ID = ORGANIZATION_MAP.CHILD_ORGANIZATION_ID WHERE PARENT_ORGANIZATION_ID = @ParentOrgID AND ORGANIZATION_TYPE <> 'OSS' ORDER BY ISOPEN DESC, SHORT_NAME */
