StoredProcedureSqlServerv2k5.body:
@NWAID int AS
DECLARE @MarineCandidateID int SET @MarineCandidateID = (SELECT MARINE_CANDIDATE_ID FROM NWA WHERE NWA_ID = @NWAID)
SELECT CONVERT(VARCHAR, CME.MEDICAL_EXAM_DATE, 106) AS EXAM_DATE, CME.MEDICAL_EXAM_TYPE, COALESCE(MEDICAL_DISPOSITION_CODE, SPACE(0)) AS MEDICAL_DISPOSITION_CODE, COALESCE(MD.MEDICAL_DISPOSITION_ID, 0) AS MEDICAL_DISPOSITION_ID, CME.MEPS_ORGANIZATION_ID, MOL.MEPS_NAME, CME.HEIGHT, CME.WEIGHT, CME.LEFT_CORR_VISUAL_ACUITY, CME.RIGHT_CORR_VISUAL_ACUITY, CME.LEFT_UNCORR_VISUAL_ACUITY, CME.RIGHT_UNCORR_VISUAL_ACUITY, CME.VISUAL_COLOR_DISPOSITION, CME.VISUAL_COLOR, CME.P_GENERAL_BODY_BUILD, CME.U_UPPER_BODY, CME.L_LOWER_BODY, CME.H_HEARING, CME.E_EYES, CME.S_MENTAL_PSYCHOLOGICAL, COALESCE(CME.CONSULTATION_TYPE, SPACE(0)) AS CONSULTATION_TYPE FROM CANDIDATE_MEDICAL_EXAM CME INNER JOIN MEPS_ORGANIZATION_LOOKUP MOL ON (CME.MEPS_ORGANIZATION_ID = MOL.MEPS_ORGANIZATION_ID AND CME.MARINE_CANDIDATE_ID = @MarineCandidateID) LEFT OUTER JOIN MEDICAL_DISPOSITION MD ON CASE CME.MEDICAL_DISPOSITION WHEN 'D' THEN 'DQ' ELSE CME.MEDICAL_DISPOSITION END = MD.MEDICAL_DISPOSITION_CODE WHERE CME.MEDICAL_EXAM_DATE = (SELECT MAX(MEDICAL_EXAM_DATE) FROM CANDIDATE_MEDICAL_EXAM WHERE MARINE_CANDIDATE_ID = @MarineCandidateID)
