StoredProcedureSqlServerv2k5.body:
@Class varchar(8), @FY int, @ClassNumber int, @OrgID int = NULL, @SortExpression nvarchar(100) = 'ScheduledShipDate' AS
--- GET THE CLASS ID DECLARE @ClassID int SELECT @ClassID = CLASS_ID FROM [dbo].[OCS_CLASS] WHERE TRAINING_TYPE_CODE = @Class AND CLASS_NUMBER = @ClassNumber AND FISCAL_YEAR = @FY
--- GET THE OCS VIEW SELECT P.PERSON_ID, N.NWA_ID, C.CONTRACT_ID, dbo.OCS.OCS_ID, P.LAST_NAME + ', ' + P.FIRST_NAME + COALESCE(SPACE(1) + P.LEGAL_MIDDLE_NAME, SPACE(0)) AS PERSON_NAME, P.SOCIAL_SECURITY_NUMBER AS SSN, MC.GENDER_CODE AS GENDER, dbo.OCS.SCHEDULED_SHIP_DATE, @Class AS TRAINING_TYPE, @ClassNumber AS CLASS_INCREMENT, dbo.OCS.REPORT_DATE, dbo.OCS.COMPANY, dbo.OCS.PLATOON, COALESCE(OCR.RESULT_DESCRIPTION, SPACE(0)) AS TRAINING_RESULT, dbo.OCS.DROP_REASON AS DROP_CODE, ODR.DROP_REASON_DESCRIPTION AS DROP_REASON, COALESCE(CONVERT(VARCHAR(2), dbo.OCS.TRAINING_DROP_WEEK), SPACE(0)) AS TRAINING_DROP_WEEK, SD.STATUS_DESCRIPTION, SD.DISPOSITION_DESCRIPTION, RO.ORGANIZATION_ID, RO.SHORT_NAME AS ORGANIZATION_NAME, RO.ORGANIZATION_TYPE FROM [dbo].[OCS] INNER JOIN [dbo].[NWA] N ON (dbo.OCS.NWA_ID = N.NWA_ID AND dbo.OCS.OCS_ID = dbo.GetMaxOCSID(dbo.OCS.NWA_ID, 'Y') AND dbo.OCS.OCS_CLASS_ID = @ClassID AND N.ORGANIZATION_OF_RECORD_ID = COALESCE(@OrgID, N.ORGANIZATION_OF_RECORD_ID)) INNER JOIN [dbo].[PERSON] P ON N.MARINE_CANDIDATE_ID = P.PERSON_ID INNER JOIN [dbo].[MARINE_CANDIDATE] MC ON P.PERSON_ID = MC.MARINE_CANDIDATE_ID INNER JOIN [dbo].[RECRUITING_ORGANIZATION] RO ON N.ORGANIZATION_OF_RECORD_ID = RO.ORGANIZATION_ID INNER JOIN [dbo].[CANDIDATE_STATUS] CS ON (N.NWA_ID = CS.NWA_ID AND CS.STATUS_TYPE = 'O' AND CS.CANDIDATE_STATUS_ID = dbo.GetMaxStatusID(CS.NWA_ID)) INNER JOIN [dbo].[STATUS_DISPOSITION] SD ON (CS.STATUS_TYPE = SD.STATUS_TYPE AND CS.STATUS_CODE = SD.STATUS_CODE AND CS.DISPOSITION_CODE = SD.DISPOSITION_CODE) LEFT OUTER JOIN [dbo].[OFFICER_CONTRACT] C ON dbo.OCS.NWA_ID = C.NWA_ID LEFT OUTER JOIN [dbo].[OCS_DROP_REASON] ODR ON dbo.OCS.DROP_REASON = ODR.DROP_REASON_CODE LEFT OUTER JOIN [dbo].[OCS_CLASS_RESULT] OCR ON dbo.OCS.CLASS_RESULT_CODE = OCR.RESULT_CODE ORDER BY CASE WHEN @SortExpression='PersonName' THEN P.LAST_NAME + ', ' + P.FIRST_NAME END ASC, CASE WHEN @SortExpression='PersonName DESC' THEN P.LAST_NAME + ', ' + P.FIRST_NAME END DESC, CASE WHEN @SortExpression='ScheduledShipDate' THEN dbo.OCS.SCHEDULED_SHIP_DATE END ASC, CASE WHEN @SortExpression='ScheduledShipDate DESC' THEN dbo.OCS.SCHEDULED_SHIP_DATE END DESC, CASE WHEN @SortExpression='ReportDate' THEN dbo.OCS.REPORT_DATE END ASC, CASE WHEN @SortExpression='ReportDate DESC' THEN dbo.OCS.REPORT_DATE END DESC, CASE WHEN @SortExpression='TrainingResult' THEN OCR.RESULT_DESCRIPTION END ASC, CASE WHEN @SortExpression='TrainingResult DESC' THEN OCR.RESULT_DESCRIPTION END DESC, CASE WHEN @SortExpression='OrgName' THEN RO.SHORT_NAME END ASC, CASE WHEN @SortExpression='OrgName DESC' THEN RO.SHORT_NAME END DESC
