StoredProcedureSqlServerv2k5.body:
@PersonID as integer, @Organiza.onID as integer
AS
SELECT P.LAST_NAME + ', ' + P.FIRST_NAME + COALESCE(SPACE(1) + P.LEGAL_MIDDLE_NAME, SPACE(0)) AS [NAME], IT.SHORT_DESCRIPTION , INQUIRY_ID, CONVERT(varchar(11), RI.INQUIRY_DATE, 13)AS INQUIRY_DATE, ID.SHORT_DESCRIPTION AS DISPOSITION, CONVERT(varchar(11), RI.DISPOSITION_DATE, 13) AS DISPOSITION_DATE, IR.SHORT_DESCRIPTION AS ACTION FROM PERSON P WITH (NOLOCK) LEFT OUTER JOIN RECRUITER_INQUIRY RI ON RI.RECRUITING_PERSON_ID= P.PERSON_ID INNER JOIN RECRUITING_PERSON RP ON RP.RECRUITING_PERSON_ID= P.PERSON_ID LEFT OUTER JOIN INQUIRY_TYPE IT ON RI.INQUIRY_CODE = IT.INQUIRY_CODE LEFT OUTER JOIN INQUIRY_RESULT_TYPE IR ON RI.RESULT_CODE = IR.RESULT_CODE LEFT OUTER JOIN INQUIRY_DISPOSITION_TYPE ID ON RI.DISPOSITION_CODE = ID.DISPOSITION_CODE WHERE RI.RECRUITING_PERSON_ID = @PersonID AND INQUIRY_ORGANIZATION_ID = @Organiza.onID ORDER BY RI.UPDATE_DATE DESC
