StoredProcedureSqlServerv2k5.body:
@SubjectId uniqueidentifier, @FromDate date.me AS
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED
SELECT count(*) FutureEventCount FROM USMC.Event e INNER JOIN USMC.EventSubject es ON e.ID = es.EventId INNER JOIN USMC.EventType et ON e.EventTypeId = et.ID and es.SubjectId = @SubjectId and e.StartDate > @FromDate and e.EventTypeId != (SELECT ID FROM USMC.EventType WHERE [TYPE] = 'Expira.on Date')
