StoredProcedureSqlServerv2k5.body:
(@CandidateWaiverID int) 

AS 

BEGIN TRANSACTION 

DELETE FROM [dbo].[CANDIDATE_PROGRAM_CRITERIA_WAIVER] WHERE CANDIDATE_WAIVER_ID = @CandidateWaiverID;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   

IF (@@ERROR <> 0) GOTO on_error; 

DELETE FROM [dbo].[CANDIDATE_CRITERIA_WAIVER] WHERE CANDIDATE_WAIVER_ID = @CandidateWaiverID; 

IF (@@ERROR <> 0) GOTO on_error; 

DELETE FROM [dbo].[CANDIDATE_WAIVER] WHERE CANDIDATE_WAIVER_ID = @CandidateWaiverID; 

IF (@@ERROR <> 0) GOTO on_error; 

COMMIT TRANSACTION 

RETURN (0); 

on_error: 
 ROLLBACK TRANSACTION 
 RETURN (1);
