StoredProcedureSqlServerv2k5.body:
(@O.cerWaiverID int)
AS
BEGIN TRANSACTION
DELETE FROM [dbo].[OFFICER_WAIVER_ATTACHMENT] WHERE OFFICER_WAIVER_ID = @O.cerWaiverID
IF (@@ERROR <> 0) GOTO on_error;
--IF THERE ARE WAIVER LETTERS UPLOADED, THE USER SHOULD NOT HAVE BEEN ABLE TO DELETE, --SO NO NEED FOR CODE TO DELETE LETTERS
DELETE FROM [dbo].[OFFICER_WAIVER] WHERE OFFICER_WAIVER_ID = @O.cerWaiverID
COMMIT TRANSACTION
RETURN (0);
on_error: ROLLBACK TRANSACTION RETURN (1);
