StoredProcedureSqlServerv2k5.body:
( 
 @ContactID uniqueiden..er, 
 @LeadID uniqueiden..er, 
 @RecruiterID uniqueiden..er, 
 @Date date.me, 
 @TimeFrameID int, 
 @ContactTypeID int, 
 @ContactSourceID int, 
 @DualContactSourceID int, 
 @ContactSourceOther varchar(30), 
 @ContactOutcomeID int, 
 @Circumstances text, 
 @Features text, 
 @Bene.ts text, 
 @IsDeleted bit, 
 @Ac.vityProspectResponseTypeID int = null 
) 
AS 
BEGIN 

 BEGIN TRANSACTION 

 IF EXISTS(SELECT ContactID FROM [dbo].[Contact] WHERE [ContactID] = @ContactID) 
 BEGIN 
  UPDATE 
   [dbo].[Contact] 
  SET  
   [LeadID] = @LeadID, 
   [RecruiterID] = @RecruiterID, 
   [Date] = @Date, 
   [TimeFrameID] = @TimeFrameID, 
   [ContactTypeID] = @ContactTypeID, 
   [ContactSourceID] = @ContactSourceID, 
   [DualContactSourceID] = @DualContactSourceID, 
   [ContactSourceOther] = @ContactSourceOther, 
   [ContactOutcomeID] = @ContactOutcomeID, 
   [Circumstances] = @Circumstances, 
   [Features] = @Features, 
   [Bene.ts] = @Bene.ts, 
   [IsDeleted] = @IsDeleted, 
   [Ac.vityProspectResponseTypeID] = @Ac.vityProspectResponseTypeID 
  WHERE  
   [ContactID] = @ContactID 
 END 
 ELSE 
 BEGIN 
  INSERT INTO [dbo].[Contact]  
  ( 
   [ContactID], 
   [LeadID], 
   [RecruiterID], 
   [Date], 
   [TimeFrameID], 
   [ContactTypeID], 
   [ContactSourceID], 
   [DualContactSourceID], 
   [ContactSourceOther], 
   [ContactOutcomeID], 
   [Circumstances], 
   [Features], 
   [Bene.ts], 
   [IsDeleted], 
   [Ac.vityProspectResponseTypeID] 
  ) 
  VALUES  
  ( 
   @ContactID, 
   @LeadID, 
   @RecruiterID, 
   @Date, 
   @TimeFrameID, 
   @ContactTypeID, 
   @ContactSourceID, 
   @DualContactSourceID, 
   @ContactSourceOther, 
   @ContactOutcomeID, 
   @Circumstances, 
   @Features, 
   @Bene.ts, 
   @IsDeleted, 
   @Ac.vityProspectResponseTypeID 
  ) 

 END 

 -- Rollback the transac.on if there were any errors 
 IF @@ERROR <> 0 
 BEGIN 
  -- Rollback the transac.on 
  ROLLBACK TRANSACTION 

  -- Raise an error and return 
  RAISERROR ('Error occurred while performing the change.', 16, 1) 
  RETURN 
 END 

 -- Rollback the transac.on if the number of rows changed does not meet our expecta.on... 
 IF @@ROWCOUNT > 1 
 BEGIN 
  -- Rollback the transac.on 
  ROLLBACK TRANSACTION 

  -- Raise an error and return 
  RAISERROR ('An unexpected number of rows were changed.', 16, 1) 
  RETURN 
 END 

 -- If we reach this point, the commands completed successfully. 
 COMMIT TRANSACTION 
END
