StoredProcedureSqlServerv2k5.body:
@NwaID INT, @ProgramBeginDate DATETIME = NULL, @ProgramGradDate DATETIME = NULL, @AdvancedProgram VARCHAR(2) = NULL, @LastEditBy VARCHAR(50) AS
IF EXISTS(SELECT 1 FROM NROTC_BOOST WHERE NWA_ID = @NwaID) BEGIN --- UPDATE THE RECORD UPDATE dbo.NROTC_BOOST SET PROGRAM_BEGIN_DATE = @ProgramBeginDate, PROGRAM_GRAD_DATE = @ProgramGradDate, ADVANCED_PROGRAM = @AdvancedProgram, USER_LOGIN = @LastEditBy, UPDATE_DATE = GETDATE() WHERE NWA_ID = @NwaID
END ELSE BEGIN --- INSERT THE RECORD INSERT INTO dbo.NROTC_BOOST (NWA_ID, PROGRAM_BEGIN_DATE, PROGRAM_GRAD_DATE, ADVANCED_PROGRAM, USER_LOGIN, UPDATE_DATE) VALUES (@NwaID, @ProgramBeginDate, @ProgramGradDate, @AdvancedProgram, @LastEditBy, GETDATE()) END
