StoredProcedureSqlServerv2k5.body:
@SMCRReserveUnitID uniqueiden..er, 
       @Organiza.onID int, 
       @SMCRReserveUnitName varchar(100), 
       @E1ThruE5Popula.on smallint, 
       @Remarks nvarchar(80), 
       @FiscalYear int, 
       @UserLogin varchar(50), 
       @PointOfContactID uniqueiden..er = null 


AS 
SET NOCOUNT ON 


IF EXISTS (SELECT '1' from [USMC].SMCRReserveUnit WHERE SMCRReserveUnitID = @SMCRReserveUnitID) 

  BEGIN 

  UPDATE [MCRISS].[USMC].[SMCRReserveUnit] 

   SET   
    [Organiza.onID] = @Organiza.onID, 
    [SMCRReserveUnitName]= @SMCRReserveUnitName, 
    [E1ThruE5Popula.on] = @E1ThruE5Popula.on, 
    [Remarks] = @Remarks, 
    [PointOfContactID] = @PointOfContactID, 
    [FiscalYear] = @FiscalYear, 
    [UserLogin]= @UserLogin,    
    [UpdateDate]= GetDate() 

    WHERE SMCRReserveUnitID = @SMCRReserveUnitID 
  END 
  ELSE 
  BEGIN 


   INSERT INTO [MCRISS].[USMC].[SMCRReserveUnit] 
    ( 
    [SMCRReserveUnitID], 
    [Organiza.onID], 
    [SMCRReserveUnitName], 
    [E1ThruE5Popula.on], 
    [Remarks], 
    [PointOfContactID], 
    [FiscalYear], 
    [UserLogin],    
    [UpdateDate] 
    ) 
   VALUES 
    ( 
    @SMCRReserveUnitID, 
    @Organiza.onID, 
    @SMCRReserveUnitName, 
    @E1ThruE5Popula.on, 
    @Remarks, 
    @PointOfContactID, 
    @FiscalYear, 
    @UserLogin,    
    GetDate() 
    )   


  END 


--- CLEAN UP 
SET NOCOUNT OFF
