StoredProcedureSqlServerv2k5.body:
@ProgramType  varchar(4), 
 @ProgramStatus char(1), 
 @ProgramDescrip.on varchar(60), 
 @ProgramCode  varchar(4), 
 @ProgramIncep.onDate date.me, 
 @UserLogin  varchar(15), 
 @ProgramExists  int output 

AS 

If Exists(Select * from Program Where @ProgramCode = Program_Code and @ProgramIncep.onDate = INCEPTION_DATE) 
BEGIN 
Select @ProgramExists = 0 
RETURN 
End 

INSERT INTO PROGRAM( 
 PROGRAM_CODE, 
 PROGRAM_TYPE, 
 PROGRAM_DESCRIPTION, 
 USER_LOGIN, 
 UPDATE_DATE, 
 INCEPTION_DATE, 
 STATUS 
 ) 
Select @ProgramCode, 
 @ProgramType, 
 @ProgramDescrip.on, 
 @UserLogin, 
 getdate(), 
 @ProgramIncep.onDate, 
 @ProgramStatus 

Select @ProgramExists = 1
