StoredProcedureSqlServerv2k5.body:
@ScheduleID int = 0, @NWAID integer, @ProcessingDate date.me, @ExistMEPSSchedule int OUTPUT, @PendingMEPS int OUTPUT AS
--Check to see if a MEPS schedule exists with the same processing date SELECT @ExistMEPSSchedule = count(*) FROM MEPS_SCHEDULE WHERE NWA_ID = @NWAID AND PROCESSING_DATE = @ProcessingDate AND SCHEDULE_ID != @ScheduleID AND DELETED != 'Y'
--Check to see if a pending MEPS schedule exists. SELECT @PendingMEPS = count(*) FROM MEPS_SCHEDULE WHERE NWA_ID = @NWAID AND PROCESSING_STATUS = 'P' AND SCHEDULE_ID != @ScheduleID AND DELETED != 'Y'
