 @IsPassing bit, 
 @AmmoCanLi. int = null, 
 @AmmoCanLi.Date date.me = null, 
 @PlankMinutes int = null, 
 @PlankSeconds int = null, 
 @Pushups int = null 
) 
AS 
BEGIN 

