  IF(@Date IS NULL AND @RunMinutes = 0 AND @RunSeconds = 0 AND COALESCE(@Pullups,0) = 0 AND COALESCE(@Hang.me,0) = 0 AND COALESCE(@Crunches,0) = 0) 
  BEGIN 
   DELETE FROM [USMC].[PhysicalFitnessTest] WHERE [ID] = @ID 
