    [RunMinutes] = @RunMinutes, 
    [RunSeconds] = @RunSeconds, 
    [Pullups] = @Pullups, 
    [Pushups] = @Pushups, 
    [Hang.me] = @Hang.me, 
    [Crunches] = @Crunches, 
    [PlankMinutes] = @PlankMinutes, 
    [PlankSeconds] = @PlankSeconds, 
    [IsPassing] = @IsPassing, 
    [AmmoCanLi.] = @AmmoCanLi. 
   WHERE  
