StoredProcedureSqlServerv2k5.body:
@MarineCandidateID int, @SchoolCode int, @Educa.onLevel varchar(2), @UserLogin varchar(15) AS
--- UPDATE THE USER_LOGIN BEFORE DELETING THE RECORD IF EXISTS (SELECT 1 FROM CANDIDATE_EDUCATION WHERE MARINE_CANDIDATE_ID = @MarineCandidateID AND SCHOOL_CODE = @SchoolCode AND EDUCATION_LEVEL = @Educa.onLevel) BEGIN UPDATE CANDIDATE_EDUCATION SET USER_LOGIN = @UserLogin WHERE MARINE_CANDIDATE_ID = @MarineCandidateID AND SCHOOL_CODE = @SchoolCode AND EDUCATION_LEVEL = @Educa.onLevel END
--- DELETE THE RECORD DELETE FROM CANDIDATE_EDUCATION WHERE MARINE_CANDIDATE_ID = @MarineCandidateID AND SCHOOL_CODE = @SchoolCode AND EDUCATION_LEVEL = @Educa.onLevel
