StoredProcedureSqlServerv2k5.body:
( @OrgId uniqueiden..er = null, @LastName varchar(75) = null, @FirstName varchar(75) = null, @SSN varchar(9) = null, @DateCreatedStart date.me = null, @DateCreatedEnd date.me = null, @Ac.onDatedStart date.me = null, @Ac.onDatedEnd date.me = null, @RecruiterId uniqueiden..er = null, @ScheduledEventsStart date.me = null, @ScheduledEventsEnd date.me = null, @EventTypeId int = null
) AS
WITH SelectedProspects AS ( SELECT ROW_NUMBER() OVER(ORDER BY PACCreateDate DESC) AS RowNum ,[PACCreateDate] ,[PACCreateDate] AS CreateDate ,prs.[ProspectID] AS ApplicantAssetID ,COALESCE(RTRIM(per.LastName), SPACE(0)) + SPACE(1) + COALESCE(RTRIM(per.FirstName), SPACE(0)) + SPACE(1) + COALESCE(RTRIM(per.MiddleName), SPACE(0)) AS ApplicantAssetName ,NULL AS SecondaryID ,rp.[rowguid] AS RecruiterID ,COALESCE(RTRIM(RP.RANK_CODE), SPACE(0)) + SPACE(1) + COALESCE(RTRIM(RCRTR.LAST_NAME), SPACE(0)) + SPACE(1) + COALESCE(RTRIM(RCRTR.FIRST_NAME), SPACE(0)) + SPACE(1) + COALESCE(RTRIM(RCRTR.LEGAL_MIDDLE_NAME), SPACE(0)) AS Recruiter ,COALESCE(RTRIM(RP.RANK_CODE), SPACE(0)) + SPACE(1) + COALESCE(RTRIM(RCRTR.LAST_NAME), SPACE(0)) AS RecruiterShortName ,pd.ProspectDisposi.onDescrip.on AS Disposi.on ,e.ID AS EventID ,e.StartDate AS NextScheduledEvent FROM [USMC].[Prospect] prs INNER JOIN [USMC].[Person] per ON per.PersonID = prs.PersonID INNER JOIN [USMC].[ProspectDisposi.on] pd ON prs.ProspectDisposi.onId = pd.ProspectDisposi.onId LEFT JOIN [MCRISS].[dbo].[RECRUITING_PERSON] rp ON rp.rowguid = prs.Recrui.ngPersonId LEFT JOIN [MCRISS].[dbo].[PERSON] RCRTR ON rp.RECRUITING_PERSON_ID = RCRTR.PERSON_ID LEFT JOIN [MCRISS].[dbo].[RECRUITING_ORGANIZATION] org ON org.ORGANIZATION_ID = rp.ORGANIZATION_ID inner JOIN [RSSOSS].[USMC].EventSubject es ON es.SubjectID = prs.ProspectId inner JOIN RSSOSS.USMC.Event e ON e.id = es.even.d and e.startdate>=getdate() and e.iscomplete=0 WHERE (org.RowGuid = @OrgId OR @OrgId IS NULL) AND (per.LastName LIKE @LastName OR @LastName IS NULL) AND (per.FirstName LIKE @FirstName OR @FirstName IS NULL) AND (per.SocialSecurityNumber LIKE @SSN OR @SSN IS NULL) AND ( (PACCreateDate >= @DateCreatedStart OR @DateCreatedStart IS NULL) AND (PACCreateDate < @DateCreatedEnd OR @DateCreatedEnd IS NULL) ) AND ( (prs.LastUpdateDate >= @Ac.onDatedStart OR @Ac.onDatedStart IS NULL) AND (prs.LastUpdateDate < @Ac.onDatedEnd OR @Ac.onDatedEnd IS NULL) ) AND (rp.RowGuid = @RecruiterId OR @RecruiterId IS NULL) AND (e.eventypeid = @EventTypeId OR @EventTypeId IS NULL) AND (pd.ProspectDisposi.onDescrip.on = 'PAC generated') AND (e.StartDate >= @ScheduledEventsStart OR @ScheduledEventsStart IS NULL) AND (e.StartDate < @ScheduledEventsEnd OR @ScheduledEventsEnd IS NULL) AND (prs.ProspectId IS NOT NULL)
)
SELECT DISTINCT* FROM SelectedProspects ORDER BY PACCreateDate DESC;
