StoredProcedureSqlServerv2k5.body:
@ProspectID uniqueiden..er AS BEGIN
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SELECT ps.ProspectSchoolId, ps.ProspectId, ps.SchoolId, HS.HIGHSCHOOL_NAME as SchoolName, ps.LastUpdateUserId, ps.Major, ps.Gradua.onDate, ps.IsFullTimeStudent, ps.CurrentGpa, ps.Cumula.veGpa, ps.Educa.onLevel, el.EDUCATION_LEVEL_DESCRIPTION AS Educa.onLevelDescrip.on, ps.Educa.onCode, ec.EDUCATION_CODE_DESCRIPTION AS Educa.onCodeDescrip.on, ps.ProjectedCompletedDate, ps.ProspectSchoolTypeId, ps.IsLawSchool, ps.CreditHours, t.ProspectSchoolType AS SchoolTypeDesc, ps.EduStatusCode, eta.Descrip.on AS EduStatusCodeDesc FROM Prospec.ng.USMC.ProspectSchool ps INNER JOIN MCRISS.dbo.HIGH_SCHOOL HS on (ps.SchoolId=HS.rowguid) LEFT OUTER JOIN MCRISS.dbo.EDUCATION_CODE ec ON ps.Educa.onCode= ec.EDUCATION_CODE LEFT OUTER JOIN MCRISS.dbo.EDUCATION_LEVEL el ON ps.Educa.onLevel = el.EDUCATION_LEVEL_CODE LEFT OUTER JOIN Prospec.ng.USMC.ProspectSchoolType t ON ps.ProspectSchoolTypeId = t.ProspectSchoolTypeId LEFT OUTER JOIN Prospec.ng.[USMC].[Educa.onStatus] eta ON ps.EduStatusCode = eta.Code WHERE ProspectId = @ProspectID END
