StoredProcedureSqlServerv2k5.body:
@MarineCandidateID int, @ProfessedExamType char(1), @ExamDate date.me, @ExamEnteredDate date.me, @UserLogin varchar(15) AS
--- UPDATE THE USER_LOGIN BEFORE DELETING THE RECORD IF EXISTS (SELECT 1 FROM CANDIDATE_PROFESSED_EXAM WHERE MARINE_CANDIDATE_ID = @MarineCandidateID AND PROFESSED_EXAM_TYPE = @ProfessedExamType AND EXAM_DATE = @ExamDate AND EXAM_ENTERED_DATE = @ExamEnteredDate) BEGIN UPDATE CANDIDATE_PROFESSED_EXAM SET USER_LOGIN = @UserLogin WHERE MARINE_CANDIDATE_ID = @MarineCandidateID AND PROFESSED_EXAM_TYPE = @ProfessedExamType AND EXAM_DATE = @ExamDate AND EXAM_ENTERED_DATE = @ExamEnteredDate END
--- DELETE THE RECORD DELETE FROM CANDIDATE_PROFESSED_EXAM WHERE MARINE_CANDIDATE_ID = @MarineCandidateID AND PROFESSED_EXAM_TYPE = @ProfessedExamType AND EXAM_DATE = @ExamDate AND EXAM_ENTERED_DATE = @ExamEnteredDate
