StoredProcedureSqlServerv2k5.body:
@ARNDiscrepancyCode   varchar(2), 
 @ARNDiscrepancyDescrip.on  varchar(50) OUTPUT 
AS 

SELECT  
 @ARNDiscrepancyDescrip.on = ARN_DISCREPANCY_DESCRIPTION  
FROM  
 ARN_DISCREPANCY  
WHERE  
 ARN_DISCREPANCY_CODE = @ARNDiscrepancyCode 

SELECT @ARNDiscrepancyDescrip.on = COALESCE(@ARNDiscrepancyDescrip.on, SPACE(0))
