StoredProcedureSqlServerv2k5.body:
@TakeOneLoca.onID uniqueiden..er, 
    @TakeOneCardID uniqueiden..er, 
 @Loca.onName varchar(50), 
 @LocStreet1 varchar(75), 
 @CityCode varchar(4), 
 @CountyCode varchar(3), 
 @StateCode varchar(2), 
 @ZipCode varchar(9), 
 @ContactFirstName varchar(25), 
 @ContactLastName varchar(25), 
 @ContactMiddleIni.al varchar(1), 
 @ContactTitle varchar(10), 
 @Phone varchar(10), 
 @EmailAddress varchar(30), 
 @Stock int, 
 @IsAc.ve bit = 1 

AS 
BEGIN 

 IF EXISTS(SELECT ID FROM RSSOSS.USMC.TakeOneLoca.on WHERE ID = @TakeOneLoca.onID) 
 BEGIN 
  UPDATE RSSOSS.USMC.TakeOneLoca.on 
     SET TakeOneCardID = @TakeOneCardID, 
      Loca.onName = @Loca.onName, 
      LocStreet1 = @LocStreet1, 
      CityCode = @CityCode, 
      CountyCode = @CountyCode, 
      StateCode = @StateCode, 
      ZipCode = @ZipCode, 
      ContactFirstName = @ContactFirstName, 
      ContactLastName = @ContactLastName, 
      ContactMiddleIni.al = @ContactMiddleIni.al, 
      ContactTitle = @ContactTitle, 
      Phone = @Phone, 
      EmailAddress = @EmailAddress, 
      Stock = @Stock, 
      IsAc.ve = @IsAc.ve 
   WHERE ID = @TakeOneLoca.onID 

 END 
 ELSE 
 BEGIN 

  INSERT INTO RSSOSS.USMC.TakeOneLoca.on(  
   ID, 
   TakeOneCardID, 
   Loca.onName, 
   LocStreet1, 
   CityCode, 
   CountyCode, 
   StateCode, 
   ZipCode, 
   ContactFirstName, 
   ContactLastName, 
   ContactMiddleIni.al, 
   ContactTitle, 
   Phone, 
   EmailAddress, 
   Stock, 
   IsAc.ve) 
  VALUES( 
   @TakeOneLoca.onID, 
   @TakeOneCardID, 
   @Loca.onName, 
   @LocStreet1, 
   @CityCode, 
   @CountyCode, 
   @StateCode, 
   @ZipCode, 
   @ContactFirstName, 
   @ContactLastName, 
   @ContactMiddleIni.al, 
   @ContactTitle, 
   @Phone, 
   @EmailAddress, 
   @Stock, 
   1)  
 END   
END
