StoredProcedureSqlServerv2k5.body:
@id uniqueiden..er, @type varchar(2), @LastUpdateUserId uniqueiden..er=null AS BEGIN
SET NOCOUNT ON;
IF @type = "PC" BEGIN UPDATE [PROSPECTING].[USMC].[ProspectComment] SET IsApproved=1, LastUpdateUserId = @LastUpdateUserId, LastUpdateDate=GetDate() WHERE prospectCommentID = @id END ELSE BEGIN UPDATE [RSSOSS].[USMC].[EVENT] SET IsApproved = 1, LastUpdateUserId = @LastUpdateUserId, LastUpdateDate=GetDate() WHERE ID = @id END
END
