StoredProcedureSqlServerv2k5.body:
@ARNStatusCode   char(1), 
 @ARNStatusDescrip.on  varchar(50) OUTPUT 
AS 

SELECT  
 @ARNStatusDescrip.on = ARN_STATUS_DESCRIPTION  
FROM  
 ARN_STATUS  
WHERE  
 ARN_STATUS_CODE = @ARNStatusCode 


SELECT @ARNStatusDescrip.on = COALESCE(@ARNStatusDescrip.on, SPACE(0))
