StoredProcedureSqlServerv2k5.body:
@Organiza.onID int, @CAIAc.vityDateBegin date.me, @CAIAc.vityDateEnd date.me AS SET NOCOUNT ON
SELECT ORGANIZATION_ID, CAI_ACTIVITY_DATE, CAI_ACTIVITY_TYPE_ID FROM CAI_ACTIVITY WHERE ORGANIZATION_ID = @Organiza.onID AND DATEDIFF(DAY, @CAIAc.vityDateBegin, CAI_ACTIVITY_DATE) >= 0 AND DATEDIFF(DAY, CAI_ACTIVITY_DATE, @CAIAc.vityDateEnd) >= 0 ORDER BY CAI_ACTIVITY_DATE ASC, CAI_ACTIVITY_TYPE_ID ASC
SET NOCOUNT OFF
