StoredProcedureSqlServerv2k5.body:
@Organiza.onID integer, @MonthBeginDate date.me, @Interviews decimal(6,1) AS
IF @Interviews = 0 SELECT 0 AS BP_TC, 0 AS BP_OC, 0 AS BP_AC, 0 AS BP_HV ELSE
SELECT SUM(TC_INTERVIEWS)/@Interviews AS BP_TC, SUM(OT_INTERVIEWS)/@Interviews AS BP_OC, SUM(AC_INTERVIEWS)/@Interviews AS BP_AC, SUM(HV_INTERVIEWS)/@Interviews AS BP_HV FROM RSS_ACTIVITY, ORGANIZATION_MAP WHERE ORGANIZATION_MAP.PARENT_ORGANIZATION_ID = @Organiza.onID AND ORGANIZATION_MAP.CHILD_ORGANIZATION_ID = RSS_ACTIVITY.ORGANIZATION_ID AND RSS_ACTIVITY.ACTIVITY_DATE BETWEEN DATEADD(m, -3, @MonthBeginDate) AND DATEADD(d, -1, @MonthBeginDate)
