StoredProcedureSqlServerv2k5.body:
@PersonID AS integer
AS
SELECT A.AEP_FILE_UPLOAD_LOG_ID AS AepID, A.[FILE_NAME] AS AepFile, A.USER_LOGIN AS UploadUser, A.UPDATE_DATE AS UploadDate, EX.USER_LOGIN AS ExportUser, EX.UPDATE_DATE AS ExportDate, EX.SUCCESSFUL_TRANSMISSION AS SuccessfulExport FROM AEP_FILE_UPLOAD_LOG A LEFT OUTER JOIN JPAS_EXPORT_LOG EX ON ( A.AEP_FILE_UPLOAD_LOG_ID = EX.AEP_FILE_UPLOAD_LOG_ID AND EX.JPAS_LOG_ID = (SELECT MAX(EX2.JPAS_LOG_ID) FROM JPAS_EXPORT_LOG EX2 WHERE EX2.AEP_FILE_UPLOAD_LOG_ID = A.AEP_FILE_UPLOAD_LOG_ID)) WHERE PERSON_ID = @PersonID and A.AEP_FILE_UPLOAD_LOG_ID = (SELECT MAX(AEP_FILE_UPLOAD_LOG_ID) FROM AEP_FILE_UPLOAD_LOG WHERE PERSON_ID = @PersonID)
