@MOSCode varchar(4), @E.ec.veDate date.me, @Gender char(1) AS
SELECT GENDER, TERM_OF_ENLISTMENT, --- SPLIT_INCREMENT, CITIZENSHIP, DRIVER_LICENSE, HIGHEST_WAIVER_LEVEL, HEIGHT_MINIMUM, HEIGHT_MAXIMUM, EDUCATION_TIER, AGE_MINIMUM, AGE_MAXIMUM, IsNull(VISION_LEFT_UNCORRECTED, ''), IsNull(VISION_RIGHT_UNCORRECTED, ''), IsNull(VISION_LEFT_CORRECTED, ''), IsNull(VISION_RIGHT_CORRECTED, ''), AFQT, GT, EL, PAOI, APT, EDPT, MM, WPMT, DLAB, CL, MEOP, IsNull(COLOR_PERCEPTION, '') --- BONUS_AMOUNT FROM MOS_CRITERIA WHERE MOS = @MOSCode AND MOS_CRITERIA_ED_DATE = @E.ec.veDate AND GENDER = @Gender
