@Occupa.onCode char(2) = NULL AS
SET @Occupa.onCode = COALESCE(@Occupa.onCode, SPACE(0))
SELECT MOS_CODE, CASE WHEN @Occupa.onCode IS NULL OR LEN(@Occupa.onCode) = 0 THEN MOS_CODE + ' - ' ELSE SPACE(0) END + SHORT_DESCRIPTION AS SHORT_DESCRIPTION, OCCUPATION_CODE FROM MOS WHERE MOS_CODE Not In(5942) and (LEN(@Occupa.onCode) > 0 AND RIGHT(MOS_CODE, 2) != '00' AND OCCUPATION_CODE = @Occupa.onCode) OR (LEN(@Occupa.onCode) = 0) ORDER BY MOS_CODE
