@MOSCode varchar(4) AS
SELECT MOS.MOS_CODE, MOS.SHORT_DESCRIPTION, MC.MOS_CRITERIA_ED_DATE, MC.GENDER, CASE WHEN MC.STATUS IS NOT NULL THEN MC.STATUS ELSE 'A' END AS STATUS FROM MOS INNER JOIN MOS_CRITERIA MC ON MOS.MOS_CODE = MC.MOS WHERE MOS.MOS_CODE = @MOSCode ORDER BY MC.MOS_CRITERIA_ED_DATE DESC
/* Select MOS.MOS_CODE, MOS.SHORT_DESCRIPTION, MC.MOS_CRITERIA_ED_DATE From MOS, MOS_CRITERIA MC Where MOS.MOS_CODE *= MC.MOS and MOS.MOS_CODE = @MOSCode Order By MC.MOS_CRITERIA_ED_DATE DESC */
