StoredProcedureSqlServerv2k5.body:
@PersonID as integer, @Organiza.onID as integer AS
SELECT RP.RECRUITING_PERSON_ID FROM RECRUITING_PERSON RP, RECRUITING_ORGANIZATION RO, RECRUITING_ROLE RR WHERE RP.RECRUITING_PERSON_ID != @PersonID AND RP.ORGANIZATION_ID = @Organiza.onID AND RP.ORGANIZATION_ID = RO.ORGANIZATION_ID AND RP.RECRUITING_ROLE_ID = RR.RECRUITING_ROLE_ID AND RR.SHORT_DESCRIPTION = 'NCOIC' AND RO.ORGANIZATION_TYPE = 'RSS' AND RP.TOUR_END_DATE IS NULL
/* SELECT RECRUITING_PERSON.RECRUITING_PERSON_ID FROM RECRUITING_PERSON, RECRUITING_ORGANIZATION, RECRUITING_ROLE WHERE RECRUITING_PERSON.RECRUITING_PERSON_ID != @PersonID AND RECRUITING_PERSON.ORGANIZATION_ID = @Organiza.onID AND RECRUITING_PERSON.ORGANIZATION_ID = RECRUITING_ORGANIZATION.ORGANIZATION_ID AND RECRUITING_PERSON.RECRUITING_ROLE_ID = RECRUITING_ROLE.RECRUITING_ROLE_ID AND SHORT_DESCRIPTION = 'NCOIC' AND ORGANIZATION_TYPE = 'RSS' */
