StoredProcedureSqlServerv2k5.body:
@PersonID as integer, @Organiza.onID as integer
AS
SELECT INQUIRY_TYPE.SHORT_DESCRIPTION, CONVERT(varchar(11), RECRUITER_INQUIRY.INQUIRY_DATE, 13), INQUIRY_DISPOSITION_TYPE.SHORT_DESCRIPTION, CONVERT(varchar(11), RECRUITER_INQUIRY.DISPOSITION_DATE, 13), INQUIRY_RESULT_TYPE.SHORT_DESCRIPTION FROM RECRUITER_INQUIRY, INQUIRY_TYPE, INQUIRY_RESULT_TYPE,INQUIRY_DISPOSITION_TYPE WHERE RECRUITING_PERSON_ID = @PersonID AND INQUIRY_ORGANIZATION_ID = @Organiza.onID AND RECRUITER_INQUIRY.INQUIRY_CODE = INQUIRY_TYPE.INQUIRY_CODE AND RECRUITER_INQUIRY.DISPOSITION_CODE = INQUIRY_DISPOSITION_TYPE.DISPOSITION_CODE AND RECRUITER_INQUIRY.RESULT_CODE = INQUIRY_RESULT_TYPE.RESULT_CODE ORDER BY RECRUITER_INQUIRY.INQUIRY_DATE
