StoredProcedureSqlServerv2k5.body:
@FiscalYear int, @Date date.me = NULL
AS
IF @Date IS NULL BEGIN SELECT PERIOD_CODE, CONVERT(VARCHAR(11), BEGIN_DATE, 106) AS BEGIN_DATE, CONVERT(VARCHAR(11), END_DATE, 106) AS END_DATE FROM PROGRAM_PERIOD WHERE FY = @FiscalYear END ELSE BEGIN SELECT PERIOD_CODE, CONVERT(VARCHAR(11), BEGIN_DATE, 106) AS BEGIN_DATE, CONVERT(VARCHAR(11), END_DATE, 106) AS END_DATE FROM PROGRAM_PERIOD WHERE FY = @FiscalYear AND @Date BETWEEN BEGIN_DATE AND END_DATE END
