StoredProcedureSqlServerv2k5.body:
@Name as varchar(60), @PersonID as int AS
SET ROWCOUNT 50
SELECT PERSON.PERSON_ID, NWA.NWA_ID, CONTRACT_ID, ISNULL(PERSON.LAST_NAME + ", ", "") + ISNULL(PERSON.FIRST_NAME + " ", "") + ISNULL(LEFT(PERSON.LEGAL_MIDDLE_NAME, 1), "") AS NAME, PERSON.SOCIAL_SECURITY_NUMBER, R2.NAME AS RS, R1.NAME AS RSS, STATUS_DISPOSITION.STATUS_DESCRIPTION, STATUS_DISPOSITION.DISPOSITION_DESCRIPTION, CONVERT(varchar(20), CANDIDATE_STATUS.STATUS_EFFECTIVE_DATE, 106) AS STATUS_EFFECTIVE_DATE FROM PERSON, NWA, CONTRACT, CANDIDATE_STATUS, STATUS_DISPOSITION, RECRUITING_ORGANIZATION R1, RECRUITING_ORGANIZATION R2, ORGANIZATION_MAP WHERE ISNULL(PERSON.LAST_NAME + ", ", "") + ISNULL(PERSON.FIRST_NAME + " ", "") + ISNULL(LEFT(PERSON.LEGAL_MIDDLE_NAME, 1), "") >= @Name AND PERSON.PERSON_ID != @PersonID AND PERSON.PERSON_ID = NWA.MARINE_CANDIDATE_ID AND NWA.NWA_ID *= CONTRACT.NWA_ID AND NWA.ORGANIZATION_OF_RECORD_ID = R1.ORGANIZATION_ID AND ORGANIZATION_MAP.CHILD_ORGANIZATION_ID = NWA.ORGANIZATION_OF_RECORD_ID AND ORGANIZATION_MAP.PARENT_ORGANIZATION_ID = R2.ORGANIZATION_ID AND CANDIDATE_STATUS.NWA_ID = NWA.NWA_ID AND CANDIDATE_STATUS.STATUS_TYPE = STATUS_DISPOSITION.STATUS_TYPE AND CANDIDATE_STATUS.STATUS_CODE = STATUS_DISPOSITION.STATUS_CODE AND CANDIDATE_STATUS.DISPOSITION_CODE = STATUS_DISPOSITION.DISPOSITION_CODE AND CANDIDATE_STATUS.STATUS_EFFECTIVE_DATE = ( SELECT MAX(STATUS_EFFECTIVE_DATE) FROM CANDIDATE_STATUS WHERE CANDIDATE_STATUS.NWA_ID = NWA.NWA_ID) ORDER BY ISNULL(PERSON.LAST_NAME + ", ", "") + ISNULL(PERSON.FIRST_NAME + " ", "") + ISNULL(LEFT(PERSON.LEGAL_MIDDLE_NAME, 1), "") ASC, PERSON.PERSON_ID ASC
