StoredProcedureSqlServerv2k5.body:
@Class varchar(8), @FY int, @ClassNumber int, @OrgID int = NULL AS
--- GET THE CLASS ID DECLARE @ClassID int SELECT @ClassID = CLASS_ID FROM OCS_CLASS WHERE TRAINING_TYPE_CODE = @Class AND CLASS_NUMBER = @ClassNumber AND FISCAL_YEAR = @FY
--- GET THE OCS VIEW SELECT P.PERSON_ID, N.NWA_ID, C.CONTRACT_ID, OCS.OCS_ID, P.LAST_NAME + ', ' + P.FIRST_NAME + COALESCE(SPACE(1) + P.LEGAL_MIDDLE_NAME, SPACE(0)) AS PERSON_NAME, P.SOCIAL_SECURITY_NUMBER AS SSN, MC.GENDER_CODE AS GENDER, CONVERT(VARCHAR(6), OCS.SCHEDULED_SHIP_DATE, 12) AS SCHEDULED_SHIP_DATE, --- OC.TRAINING_TYPE_CODE AS TRAINING_TYPE, --- OC.CLASS_NUMBER AS CLASS_INCREMENT, @Class AS TRAINING_TYPE, @ClassNumber AS CLASS_INCREMENT, --- COALESCE(CONVERT(VARCHAR(6), OCS.REPORT_DATE, 12), SPACE(0)) AS REPORT_DATE, COALESCE(CONVERT(VARCHAR(11), OCS.REPORT_DATE, 106), SPACE(0)) AS REPORT_DATE, OCS.COMPANY, OCS.PLATOON, COALESCE(OCR.RESULT_DESCRIPTION, SPACE(0)) AS TRAINING_RESULT, OCS.DROP_REASON AS DROP_CODE, ODR.DROP_REASON_DESCRIPTION AS DROP_REASON, COALESCE(CONVERT(VARCHAR(2), OCS.TRAINING_DROP_WEEK), SPACE(0)) AS TRAINING_DROP_WEEK, SD.STATUS_DESCRIPTION, SD.DISPOSITION_DESCRIPTION, RO.ORGANIZATION_ID, RO.SHORT_NAME AS ORGANIZATION_NAME, RO.ORGANIZATION_TYPE FROM OCS INNER JOIN NWA N ON (OCS.NWA_ID = N.NWA_ID AND OCS.OCS_ID = dbo.GetMaxOCSID(OCS.NWA_ID, 'Y') AND OCS.OCS_CLASS_ID = @ClassID AND N.ORGANIZATION_OF_RECORD_ID = COALESCE(@OrgID, N.ORGANIZATION_OF_RECORD_ID)) INNER JOIN PERSON P ON N.MARINE_CANDIDATE_ID = P.PERSON_ID INNER JOIN MARINE_CANDIDATE MC ON P.PERSON_ID = MC.MARINE_CANDIDATE_ID INNER JOIN RECRUITING_ORGANIZATION RO ON N.ORGANIZATION_OF_RECORD_ID = RO.ORGANIZATION_ID INNER JOIN CANDIDATE_STATUS CS ON (N.NWA_ID = CS.NWA_ID AND CS.STATUS_TYPE = 'O' AND CS.CANDIDATE_STATUS_ID = dbo.GetMaxStatusID(CS.NWA_ID)) INNER JOIN STATUS_DISPOSITION SD ON (CS.STATUS_TYPE = SD.STATUS_TYPE AND CS.STATUS_CODE = SD.STATUS_CODE AND CS.DISPOSITION_CODE = SD.DISPOSITION_CODE) LEFT OUTER JOIN OFFICER_CONTRACT C ON OCS.NWA_ID = C.NWA_ID LEFT OUTER JOIN OCS_DROP_REASON ODR ON OCS.DROP_REASON = ODR.DROP_REASON_CODE LEFT OUTER JOIN OCS_CLASS_RESULT OCR ON OCS.CLASS_RESULT_CODE = OCR.RESULT_CODE
