StoredProcedureSqlServerv2k5.body:
AS
SELECT OCCUPATION_CODE, MOS_CODE + ' - ' + SHORT_DESCRIPTION AS SHORT_DESCRIPTION FROM MOS WHERE RIGHT(MOS_CODE, 2) = '00' ORDER BY SHORT_DESCRIPTION
