StoredProcedureSqlServerv2k5.body:
@EventId uniqueidentifier AS BEGIN
SELECT DISTINCT RTrim(rp.Rank_Code) as RankCode, RTrim(p.Last_Name) as LastName, RTrim(p.First_Name) as FirstName, RTrim(p.LEGAL_MIDDLE_NAME) as MiddleName, rp.rowguid as RecruiterID FROM USMC.EventPlanOwners eo INNER JOIN MCRISS.dbo.Recrui.ng_Person rp ON rp.rowguid = eo.OwnerId INNER JOIN MCRISS.dbo.Person p ON rp.Recrui.ng_Person_ID = p.Person_ID WHERE eo.EventId = @EventId
END
