StoredProcedureSqlServerv2k5.body:
@EventId uniqueidentifier AS
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED
SELECT EP.ID, EP.OwnerId, EP.Organiza.onId, ES.Id EventSourceId, ES.Name EventSourceName, ET.Id EventTypeId, ET.Type EventTypeName, EP.Loca.on, EP.Comments, EP.IsComplete, EP.StartDate, EP.EndDate, EP.MISApprovedEventStartDate, EP.MISApprovedEventEndDate, EP.WISApprovedEventStartDate, EP.WISApprovedEventEndDate, EP.EventTypeOther, EP.LastUpdateDate, EP.LastUpdateUserId, EP.Remarks, EP.EventPrecision, EP.SeriesID, EP.IsApproved FROM [USMC].[EventPlan] EP JOIN [USMC].[EventSource] AS ES ON EP.EventSourceId = ES.ID JOIN [USMC].[EventType] AS ET ON EP.EventTypeId = ET.ID WHERE EP.Id = @EventId
