@EventId uniqueidentifier AS
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED
SELECT E.POC, E.POCPhone, E.POCFax, E.CareerFairName, E.Registra.onDeadline, E.Adver.singFlag, E.LinkedEvents, E.Cost, E.Remarks, E.CAC, E.Descrip.on, E.Spent FROM [USMC].[CareerFairEvent] E WHERE E.ID = @EventId
