StoredProcedureSqlServerv2k5.body:
@ProgramID int, @E.ec.veDate date.me
AS
SELECT GENDER, TERM_OF_ENLISTMENT, CITIZENSHIP, DRIVER_LICENSE, IsNull(HIGHEST_WAIVER_LEVEL, ''), HEIGHT_MINIMUM, HEIGHT_MAXIMUM, EDUCATION_TIER, AGE_MINIMUM, AGE_MAXIMUM, IsNull(VISION_LEFT_UNCORRECTED, ''), IsNull(VISION_RIGHT_UNCORRECTED, ''), IsNull(VISION_LEFT_CORRECTED, ''), IsNull(VISION_RIGHT_CORRECTED, ''), AFQT, GT, EL, PAOI, APT, EDPT, MM, WPMT, DLAB, CL, MEOP, IsNull(COLOR_PERCEPTION, ''), BONUS_AMOUNT FROM PROGRAM_CRITERIA WHERE PROGRAM_ID = @ProgramID AND PROGRAM_CRITERIA_ED_DATE = @E.ec.veDate
