StoredProcedureSqlServerv2k5.body:
@WaiverCatId int
AS
SELECT DISTINCT WT.WAIVER_TYPE_ID, WT.WAIVER_TYPE_CODE + ' - ' + WT.WAIVER_TYPE_DESC AS WAIVER_TYPE_DESC, WT.WAIVER_TYPE_CODE FROM WAIVER_TYPE WT, WAIVER W WHERE W.WAIVER_CATEGORY_ID = @WaiverCatId AND W.WAIVER_TYPE_ID = WT.WAIVER_TYPE_ID ORDER BY WT.WAIVER_TYPE_DESC
