StoredProcedureSqlServerv2k5.body:
@PersonID int = 0, @SocialSecurityNumber varchar(9) = null, @UserLogin varchar(15) AS
IF EXISTS (SELECT 1 FROM PERSON WHERE PERSON_ID = @PersonID) BEGIN --- UPDATE BASED ON PERSON ID (THIS WILL ALLOW FOR SSN CHANGES) UPDATE PERSON SET SOCIAL_SECURITY_NUMBER = @SocialSecurityNumber, USER_LOGIN = @UserLogin, UPDATE_DATE = GETDATE() WHERE PERSON_ID = @PersonID
END
