StoredProcedureSqlServerv2k5.body:
@Educa.onLevelCode  char(2), 
 @Educa.onCode   char(1), 
 @EligibleToShip   char(1) OUTPUT 
AS 

--- RETRIEVE THE ELIGIBLE TO SHIP FLAG 
SELECT @EligibleToShip = COALESCE((SELECT ELIGIBLE_TO_SHIP FROM EDUCATION_LEVEL_CODE WHERE EDUCATION_LEVEL_CODE = @Educa.onLevelCode AND EDUCATION_CODE = @Educa.onCode), 'N')
