StoredProcedureSqlServerv2k5.body:
@PersonID int = 0, @FirstName varchar(20) = null, @LegalMiddleName varchar(20) = null, @LastName varchar(20) = null, @Su.xCD varchar(5) = null, @UserLogin varchar(15) AS
IF EXISTS (SELECT 1 FROM PERSON WHERE PERSON_ID = @PersonID) BEGIN --- UPDATE BASED ON PERSON ID (THIS WILL ALLOW Name CHANGES) UPDATE PERSON SET FIRST_NAME = @FirstName, LEGAL_MIDDLE_NAME = @LegalMiddleName, LAST_NAME = @LastName, SUFFIX_CD = @Su.xCD, USER_LOGIN = @UserLogin, UPDATE_DATE = GETDATE() WHERE PERSON_ID = @PersonID
END
