StoredProcedureSqlServerv2k5.body:
@NWAID int, @bKYBWaiver char(1) OUTPUT
AS
SELECT @bKYBWaiver = CASE WHEN EXISTS (SELECT 1 FROM CANDIDATE_WAIVER CW INNER JOIN WAIVER W ON (CW.WAIVER_ID = W.WAIVER_ID AND CW.NWA_ID = @NWAID AND CW.WAIVER_STATUS_CODE = 'A') INNER JOIN WAIVER_CATEGORY WC ON (W.WAIVER_CATEGORY_ID = WC.WAIVER_CATEGORY_ID AND WC.WAIVER_CATEGORY_CODE = 'K') INNER JOIN WAIVER_TYPE WT ON (W.WAIVER_TYPE_ID = WT.WAIVER_TYPE_ID AND WT.WAIVER_TYPE_CODE = 'Y') INNER JOIN WAIVER_LEVEL WL ON (W.WAIVER_LEVEL_ID = WL.WAIVER_LEVEL_ID AND WL.WAIVER_LEVEL_CODE = 'B')) THEN 'Y' ELSE 'N' END
