StoredProcedureSqlServerv2k5.body:
@DependentID  int = NULL, 
 @PersonID   int, 
 @LastName   varchar(20), 
 @FirstName   varchar(30), 
 @MiddleName  varchar(20), 
 @Su.xCd   varchar(5), 
 @DateOfBirth  date.me, 
 @DependentType  varchar(2), 
 @Veri.ca.on  char(1), 
 @UserLogin  varchar(15) 

AS 

BEGIN 
 IF EXISTS (SELECT 1 FROM CANDIDATE_DEPENDENT WHERE DEPENDENT_ID = @DependentID AND MARINE_CANDIDATE_ID =@PersonID) 

  BEGIN 

   UPDATE  CANDIDATE_DEPENDENT 
   SET  DATE_OF_BIRTH = @DateOfBirth, 
    LAST_NAME = @LastName, 
    FIRST_NAME = @FirstName, 
    MIDDLE_NAME = @MiddleName, 
    SUFFIX_CD = @Su.xCd, 
    DEPENDENT_TYPE = @DependentType, 
    VERIFICATION = @Veri.ca.on, 
    USER_LOGIN = @UserLogin,  
    UPDATE_DATE = GETDATE() 
   WHERE  DEPENDENT_ID = @DependentID  
    AND MARINE_CANDIDATE_ID = @PersonID 
  END 
 ELSE 

  BEGIN 

   INSERT INTO CANDIDATE_DEPENDENT (MARINE_CANDIDATE_ID, 
       LAST_NAME, 
       FIRST_NAME, 
       MIDDLE_NAME, 
       SUFFIX_CD,  
       DEPENDENT_TYPE, 
       VERIFICATION, 
       DATE_OF_BIRTH, 
       USER_LOGIN,  
       UPDATE_DATE)  
   VALUES  
       (@PersonID, 
       @LastName, 
       @FirstName, 
       @MiddleName, 
       @Su.xCd,  
       @DependentType, 
       @Veri.ca.on, 
       @DateOfBirth, 
       @UserLogin,  
       GETDATE()) 

  END 
END
