StoredProcedureSqlServerv2k5.body:
@Organiza.onID int, @CAIAc.vityDate date.me, @CAIAc.vityTypeID int, @CAIAc.vityTypeSelected char(1), @UserLogin varchar(15) AS SET NOCOUNT ON
IF NOT EXISTS (SELECT 1 FROM CAI_ACTIVITY WHERE @Organiza.onID = ORGANIZATION_ID AND @CAIAc.vityDate = CAI_ACTIVITY_DATE AND @CAIAc.vityTypeID = CAI_ACTIVITY_TYPE_ID) BEGIN -- CASE 1: DOESN'T EXIST AND BOX IS SELECTED = INSERT RECORD IF @CAIAc.vityTypeSelected = 'Y' BEGIN INSERT INTO CAI_ACTIVITY(ORGANIZATION_ID, CAI_ACTIVITY_DATE, CAI_ACTIVITY_TYPE_ID, USER_LOGIN, UPDATE_DATE) VALUES(@Organiza.onID, @CAIAc.vityDate, @CAIAc.vityTypeID, @UserLogin, getdate()) END -- CASE 2: DOESN'T EXIST AND BOX IS NOT SELECTED = DON'T DO ANYTHING END ELSE BEGIN -- CASE 3: DOES EXIST AND BOX IS NOT SELECTED = DELETE RECORD IF @CAIAc.vityTypeSelected = 'N' BEGIN DELETE FROM CAI_ACTIVITY WHERE ORGANIZATION_ID = @Organiza.onID AND CAI_ACTIVITY_DATE = @CAIAc.vityDate AND CAI_ACTIVITY_TYPE_ID = @CAIAc.vityTypeID END -- CASE 4: DOES EXIST AND BOX IS SELECTED = DON'T DO ANYTHING END
SET NOCOUNT OFF
