StoredProcedureSqlServerv2k5.body:
@PersonID int, 
 @AddressType varchar(10), 
 @StreetAddress varchar(50), 
 @CityCode varchar(4), 
 @CountyCode varchar(3), 
 @StateCode varchar(2), 
 @ZipCode varchar(9), 
 @UserLogin varchar(15) 
AS 

IF EXISTS(SELECT 1 FROM CANDIDATE_ADDRESS WHERE MARINE_CANDIDATE_ID = @PersonID AND ADDRESS_TYPE = @AddressType) 
 BEGIN 

 UPDATE CANDIDATE_ADDRESS 
 SET STREET_ADDRESS = @StreetAddress, 
 CITY_CODE = @CityCode, 
 COUNTY_CODE = @CountyCode, 
 STATE_CODE = @StateCode, 
 ZIPCODE = @ZipCode, 
 USER_LOGIN = @UserLogin, 
 UPDATE_DATE = GetDate() 
 WHERE MARINE_CANDIDATE_ID = @PersonID AND  
 ADDRESS_TYPE = @AddressType 

 END 
ELSE 
 BEGIN 

 INSERT INTO CANDIDATE_ADDRESS 
 (MARINE_CANDIDATE_ID, 
 ADDRESS_TYPE, 
 STREET_ADDRESS, 
 CITY_CODE, 
 COUNTY_CODE, 
 STATE_CODE, 
 ZIPCODE, 
 USER_LOGIN, 
 UPDATE_DATE) 
 VALUES(@PersonID, 
 @AddressType, 
 @StreetAddress, 
 @CityCode, 
 @CountyCode, 
 @StateCode, 
 @ZipCode, 
 @UserLogin, 
 GetDate()) 

 END
