StoredProcedureSqlServerv2k5.body:
@ContractID int, @ProgramMOSCode varchar(25), @UserLogin varchar(25) AS
IF EXISTS (SELECT 1 FROM PROGRAM_MOS_CODES WHERE CONTRACT_ID = @ContractID) BEGIN UPDATE PROGRAM_MOS_CODES SET PROGRAM_MOS_CODE = @ProgramMOSCode, USER_LOGIN = @UserLogin, UPDATE_DATE = GETDATE() WHERE CONTRACT_ID = @ContractID
END ELSE BEGIN INSERT INTO PROGRAM_MOS_CODES (CONTRACT_ID, PROGRAM_MOS_CODE, USER_LOGIN, UPDATE_DATE) VALUES (@ContractID, @ProgramMOSCode, @UserLogin, GETDATE()) END
